flex: grid-column-flex($columns);
// Gutters
@if type-of($gutter) == "map" {
    @each $br, $value in $gutter {
        $padding: ($value / 2);

        @media (min-width:map-get($dc-grid-breakpoints, $br)) {
            padding-right: $padding;
            padding-left: $padding;
        }
    }
}

